<style>
  #ship {
    transform: translateX(12%);
    animation: animate-ship 5s 1s forwards ease;
  }

  @keyframes animate-ship {
    100% {
      transform: translateX(0);
    }
  }

  #gradient .start {
    stop-color: var(--primary-bg-color);
    stop-opacity: 1;
  }

  #gradient .end {
    stop-color: var(--secondary-bg-color);
    stop-opacity: 0.3;
  }

  .letter {
    fill-opacity: 0;
    stroke-dasharray: 600;
    stroke-dashoffset: 600;

    filter: drop-shadow(3px 3px 2px rgba(0, 0, 0, 0.2));
    fill: url(#gradient);
    stroke: rgba(255, 255, 255, 0.7);
    stroke-width: 2;
    stroke-linecap: round;
    stroke-linejoin: round;
    transition: all ease 1s;
  }

  .letter:hover {
    cursor: pointer;
    stroke-width: 4;
  }

  #letter-p {
    animation: animate-letter 4s 1s forwards ease;
  }

  #letter-e {
    animation: animate-letter 3.5s 2s forwards ease;
  }

  #letter-n {
    animation: animate-letter 3s 3s forwards ease;
  }

  @keyframes animate-letter {
    40% {
      fill-opacity: 0;
    }
    100% {
      fill-opacity: 1;
      stroke-dashoffset: 0;
    }
  }

  #hull {
    filter: drop-shadow(3px 3px 2px rgba(0, 0, 0, 0.2));
    fill: url(#gradient);
    stroke: rgba(255, 255, 255, 0.7);
    stroke-width: 2;
    stroke-linecap: round;
    stroke-linejoin: round;
    transition: all ease 1s;
    animation: animate-hull 7s forwards ease;
  }

  #hull:hover {
    cursor: pointer;
    stroke-width: 4;
  }

  @keyframes animate-hull {
    0% {
      fill-opacity: 0;
      stroke-dasharray: 3000;
      stroke-dashoffset: 3000;
    }
    100% {
      fill-opacity: 1;
      stroke-dashoffset: 0;
    }
  }

  #jib {
    filter: drop-shadow(3px 3px 2px rgba(0, 0, 0, 0.2));
    fill: url(#gradient);
    stroke: rgba(255, 255, 255, 0.7);
    stroke-width: 2;
    stroke-linecap: round;
    stroke-linejoin: round;
    transition: all ease 1s;
    animation: animate-sail 8s forwards ease-out;
  }

  #jib:hover {
    cursor: pointer;
    stroke-width: 4;
  }

  #mainsail {
    filter: drop-shadow(3px 3px 2px rgba(0, 0, 0, 0.2));
    fill: url(#gradient);
    stroke: rgba(255, 255, 255, 0.7);
    stroke-width: 2;
    stroke-linecap: round;
    stroke-linejoin: round;
    transition: all ease 1s;
    animation: animate-sail 6s forwards ease-out;
  }

  #mainsail:hover {
    cursor: pointer;
    stroke-width: 4;
  }

  @keyframes animate-p {
    100% {
      stroke-width: 20;
    }
  }

  @keyframes animate-sail {
    0% {
      fill-opacity: 0;
      stroke-dasharray: 4000;
      stroke-dashoffset: 4000;
    }
    100% {
      fill-opacity: 1;
      stroke-dashoffset: 0;
    }
  }
</style>